const App = () => {
  const [zipCode, setZipCode] = useState('');
  const [temperature, setTemperature] = useState(null);
  const [error, setError] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(null);

    try {
      const response = await fetch(`http://api.openweathermap.org/data/2.5/weather?zip=${zipCode},us&units=imperial&appid=YOUR_API_KEY`);
      const data = await response.json();
      setTemperature(data.main.temp);
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Enter zip code" value={zipCode} onChange={(event) => setZipCode(event.target.value)} />
        <button type="submit">Get Temperature</button>
      </form>
      {error && <p>{error}</p>}
      {temperature && <p>Temperature: {temperature}°F</p>}
    </div>
  );
};

export default App;
